mapper.update:
UPDATE post 
SET comment_count = (
    SELECT COUNT(*) 
    FROM comment 
    WHERE post_id = #{postId} 
      AND deleted = 0 
      AND status = 'APPROVED'
)
WHERE id = #{postId}

mapper.select:
SELECT COUNT(*) > 0
FROM comment_like
WHERE comment_id = #{commentId} AND user_id = #{userId}

SELECT c.id, c.post_id as postId, p.title as postTitle, c.user_id as userId, u.display_name as userName, '' as userAvatar, c.parent_id as parentId, c.content, c.status, c.like_count as likeCount, FALSE as isLiked, c.created_at as createdAt, c.updated_at as updatedAt, (SELECT COUNT(*) FROM comment cc WHERE cc.parent_id = c.id AND cc.deleted = 0) as replyCount FROM comment c LEFT JOIN post p ON c.post_id = p.id LEFT JOIN users u ON c.user_id = u.id WHERE c.deleted = 0
AND c.status = #{status}
ORDER BY c.created_at DESC
