mapper.select:
SELECT COUNT(*)
FROM update_request
WHERE post_id = #{postId} 
  AND user_id = #{userId}
  AND deleted = 0
  AND DATE(created_at) = CURDATE()

SELECT ur.id, ur.post_id as postId, p.title as postTitle, ur.user_id as userId, u.display_name as userName, ur.message, ur.type, ur.created_at as createdAt FROM update_request ur LEFT JOIN post p ON ur.post_id = p.id LEFT JOIN users u ON ur.user_id = u.id WHERE ur.deleted = 0
AND ur.type = #{type}
ORDER BY ur.created_at DESC
